import React from "react"
import News from './News.js'
import { connect } from 'react-redux'
import { fetchCities } from './actions/NewsAction.js'


// {this.props.news.map(article=>
//     <News 
//     key={article.id} 
//     article={article}/>)}

class NewsContainer extends React.Component{

    state={
        loading: true
    }

    componentDidMount(){
        this.props.fetchCities()
        .then(()=>{
            this.setState({
                loading: false
            })
        })

    }

    render(){
        return(
            <div> 
                <h3>{this.props.likes}</h3>
                <button onClick={this.props.addLikes}>LIKE</button>
                <input name="input" onChange={(e)=>this.props.changeInput(e.target.value)}/>
                <h3>{this.props.input}</h3>
            </div>
        )
    }
}

function mapStateToProps(state){
    return{
        likes: state.likes, 
        input: state.input
    }
}

function mapDispatchToProps(dispatch){
    return{
        addLikes: ()=>{
            dispatch({type: "ADD_LIKES"})
        },
        changeInput: (input)=>{
            dispatch({type: "CHANGE_INPUT", payload: input})
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps, {fetchCities})(NewsContainer);
