  function UserReducer(state = {}, action){
    switch (action.type) {
      case "LOGIN":
          return {...state, news: action.payload }
      case "SIGNUP":
      case "PERSIST":
        return payload;
      case "LOGOUT":
        return {};
      default:
        return state;
    }
  };


export default UserReducer
