import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import { createStore } from 'redux'
import * as serviceWorker from './serviceWorker';

// store is used to manage state
// contains state as an object and functions that manipulate/read state

// reducer: is used to control changes to state
// whatever is returned from the reducer becomes state
const defaultState = {
    news: [], 
    filter: "",
    loading: true
}


// type is used to determine what to do in reducer
// payload is used to determine what new data needs to be used to change state
function reducer(state=defaultState, action){
    console.log("STATE in reducer", state)
    console.log("ACTION in reducer", action)

    switch(action.type){ //action.type is really just a string and MUST be called type
        case "CHANGE_LOADING":
            return {...state, loading: false} // making a copy, using spread operator to preserve old state but only changing what we want to change
        case "ADD_CATEGORY":
            return {...state, news: [...state.news, action.payload]} //using spread operator here bc adding to the array
        case "FILTER_TECH":
            return {...state, filter: action.payload} // do not need spread operator bc only changing state, not adding to it
            default:
                return state //whatever is returned from the reducer becomes state, its like setting state
    }
}

const store = createStore(reducer)

// dispatch is the functions used to write to the state, 
// dipatch takes objects as arguments called "action"
// the action must have at least a property called "type"
// calling dispatch tells redux to call your reducer, this will initate your reducer
console.log("reading state before dispatch", store.getState())


// the type here is exactly the same as what is passed in action on line 18
const newsCategory = {name: "Tech"}
const filterTech = {title: "hello"}
store.dispatch({type: "CHANGE_LOADING"}) 
store.dispatch({type: "ADD_CATEGORY", payload: newsCategory})
store.dispatch({type: "CHANGE_FILTER", payload:filterTech })
// payload can be any data type; object, array, string, etc
// when you hook up react with redux and  make a fetch; calling dispatch and then passing in (fetch)data as a payload and using that to write to state in reducer
// everytime you call dispatch below, the reducer is called

// type is used to determine what to do in reducer
// payload is used to determine what new dta needs to be used to change state
// data is always action.payload => payloads are like sources of information


console.log("reading state after dispatch", store.getState())

ReactDOM.render(<App />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
