// const initialState = {};

export default function UserReducer(state = {}, { type, payload }) {
    switch (type) {
      case "LOGIN":
      case "SIGNUP":
      case "PERSIST":
        return payload;
      case "LOGOUT":
        return {};
      default:
        return state;
    }
  };


